interface IRawEntry {
        name?:string;
        version?:string;
        browsers?:string;
        desc?:string;
        ref?:string;
        restriction?:string;
        syntax?:string;
        values?:any[];
}

export interface Browser {
        name:string;
        version:number;
}

export interface Value {
        name:string;
        version:string;
}

export interface IEntry {
        getName():string;
        getVersion():number;
        getBrowsers():Browser[];
        getDescription():string;
        getValues():Value[];
}

class Entry implements IEntry {

        constructor(private data:IRawEntry) {
                // empty
        }
        
        getName():string {
                return this.data.name;
        }
        getVersion():number {
                return Number(this.data.version);
        }
        getBrowsers():Browser[] {
                return [];
        }
        getDescription():string {
                return this.data.desc;
        }
        getValues():Value[] {
                return [];
        }
}

var atDirectives = <IRawEntry[]> data.css.atdirectives;
var pseudoClasses = <IRawEntry[]> data.css.pseudoclasses;
var pseudoElements = <IRawEntry[]> data.css.pseudoelements;
var properties = <IRawEntry[]> data.css.properties;

var propertySet:{[name:string]:IEntry;};

export function getProperties():{[name:string]:IEntry;} {
        if(!propertySet) {
                propertySet = {};
                for(var i = 0, len = properties.length; i < len; i++) {
                        var rawEntry = properties[i];
                        propertySet[rawEntry.name] = new Entry(rawEntry);               
                }
        }
        return propertySet;
}
